package com.example.smartoffice3.activitys;import android.os.Bundle;import android.widget.TextView;import androidx.appcompat.app.AppCompatActivity;import com.example.smartoffice3.R;import com.smartarmenia.dotnetcoresignalrclientjava.HubConnection;import com.smartarmenia.dotnetcoresignalrclientjava.HubConnectionListener;import com.smartarmenia.dotnetcoresignalrclientjava.HubEventListener;import com.smartarmenia.dotnetcoresignalrclientjava.HubMessage;import com.smartarmenia.dotnetcoresignalrclientjava.WebSocketHubConnectionP2;public class Room extends AppCompatActivity {    public static String ID_DEVICE;    private TextView textView;    final HubConnection connection = new WebSocketHubConnectionP2("http://localhost:7827/api/","");    private HubConnectionListener listener = new HubConnectionListener() {        @Override        public void onConnected() {        }        @Override        public void onDisconnected() {        }        @Override        public void onMessage(HubMessage message) {        }        @Override        public void onError(Exception exception) {        }    };    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        connection.connect();        connection.addListener(listener);        connection.subscribeToEvent("m", (HubEventListener) listener);        connection.invoke("Method","api/sensor/0");        setContentView(R.layout.activity_room);        textView=findViewById(R.id.name_room);        textView.setText(ID_DEVICE);    }}